INSERT INTO `yf_admin_menu` VALUES ('12012', '12002', '商品分类', '', '3870', 'Goods_Cat', 'cat', '', '<li>当店主添加商品时可选择商品分类，用户可根据分类查询商品列表</li>\n   <li>“编辑分类导航”功能可以设置前台左上侧商品分类导航的相关信息，可以设置分类前图标、分类别名、推荐分类、推荐品牌以及两张广告图片。</li>\n   <li>修改完成后,必须去清楚缓存,否则无法立即生效。</li>', '0', '0000-00-00 00:00:00');
INSERT INTO `yf_admin_menu` VALUES ('12013', '12003', '管理', '', '3370', 'Goods_Brand', 'brand', '', '<li>当店主添加商品时可选择商品品牌，用户可根据品牌查询商品列表</li>\n            <li>被推荐品牌，将在前台品牌推荐模块处显示</li>\n            <li>在品牌列表页面，品牌将按类别分组，即具有相同类别的品牌为一组，品牌类别与品牌分类无联系</li>\n            <li>上传品牌图片时，请上传宽度不低于240像素，高度不低于200像素的图片</li>', '0', '0000-00-00 00:00:00');
INSERT INTO `yf_admin_menu` VALUES ('12014', '12003', '待审核', '', '0', 'Goods_Brand', 'brands', '', '<li>当店主添加商品时可选择商品品牌，用户可根据品牌查询商品列表</li>\n            <li>被推荐品牌，将在前台品牌推荐模块处显示</li>\n            <li>在品牌列表页面，品牌将按类别分组，即具有相同类别的品牌为一组，品牌类别与品牌分类无联系</li>', '0', '0000-00-00 00:00:00');
INSERT INTO `yf_admin_menu` VALUES ('12015', '12004', '类型', '', '0', 'Goods_Type', 'type', '', '<li>当管理员添加商品分类时需选择类型。前台分类下商品列表页通过类型\n    生成商品检索，方便用户搜索需要的商品。</li>', '0', '0000-00-00 00:00:00');
