INSERT INTO `yf_admin_menu` VALUES ('16009', '16002', '实物订单结算', '', '8520', 'Operation_Settlement', 'settlement', '', '<li>账单计算公式：订单金额(含运费) + 红包金额 - 佣金金额 - 退还红包金额 - 退单金额 + 退还佣金 - 店铺消费 - 分销佣金总额订单金额(含运费) + 红包金额 - 佣金金额 - 退还红包金额 - 退单金额 + 退还佣金 - 店铺消费 - 分销佣金总额</li>\n            <li>账单处理流程为：系统出账 > 商家确认 > 平台审核 > 财务支付(完成结算) 4个环节，其中平台审核和财务支付需要平台介入，请予以关注</li>', '0', '0000-00-00 00:00:00');
INSERT INTO `yf_admin_menu` VALUES ('16010', '16002', '虚拟订单结算', '', '8540', 'Operation_Settlement', 'settlement', 'otyp=1', '<li>账单计算公式：订单金额(含运费) - 佣金金额 - 退单金额 + 退还佣金</li>\n            <li>账单处理流程为：系统出账 > 商家确认 > 平台审核 > 财务支付(完成结算) 4个环节，其中平台审核和财务支付需要平台介入，请予以关注</li>', '0', '0000-00-00 00:00:00');
